import React,{Component} from 'react'
import TeamMember from './TeamMember'
import memberInfo from './memberInfo'


class Team extends Component{
    constructor(){
        super()
        this.state = { 
            memberInfo : memberInfo,
            members: [],
        }

        this.clickEvent= this.clickEvent.bind
    }


    clickEvent (){
        this.state.members.push('test');
    }

    
    mapping(x){
            const members = x.map( member =>
                <TeamMember
                    key={member.id}
                    img={member.img}
                    name={member.name}
                    position={member.position}
                    phone={member.phone}
                    email={member.email}
                    facebook={member.facebook}
                />);
            return members
    }


    render(){
        return(
            <div className="row">
                <button className='btn btn-primary btn-lg btn-block' onClick={this.clickEvent}>Click Me!</button>
                {this.mapping(this.state.memberInfo)}
                </div>
        )
    }
}

export default Team;
