import React from 'react'
import './Services.css'
import HeartEmoji from '../../img/heartemoji.png'
import Glasses from '../../img/glasses.png'
import Humble from '../../img/humble.png'
import Card from '../Card/Card'
import resume from './resume.pdf'
import {themeContext} from '../../Context'
import { useContext } from "react";
import {motion} from 'framer-motion' 


const Services = () => {
  const theme = useContext (themeContext);
  const darkMode = theme.state.darkMode;

  return (
    <div className="services">
        {/* left side */}
        <div className="awesome">
            <span style={{color:darkMode?'white':''}}>My Awesome</span>
            <span>Services</span>
            <span>Design can be art. Design can be aesthetics. Design is so simple,
                <br/>
                 that's why it is so complicated.
                 </span>
            <a href={resume} download>
            <button className="button s-button">Download Cv</button>  
            </a>
            <div className="blur s-blur1" style={{background:"#ABF1FF94"}}></div>
        </div>
        {/* right side */}
        <div className="cards">
            <motion.div
            initial={{}}
            whileInView
            style={{left: '16rem'}}>
                <Card
                emoji={HeartEmoji}
                heading={'Design'} 
                detail={'Figma, Sketch, PhotoSHop, Adobe, Adobe xd'}
                />
            </motion.div>
            {/* second card */}
            <div style={{top:"12rem",left:"-2rem"}} >
                <Card
                emoji={Glasses}
                heading={'Developer'}
                detail={'Html, Css, JavaScript, php, React, NodeJs'}
                />
            </div>
            {/* third card */}
            <div style={{top:"19rem",left:"14rem"}}>
                <Card
                emoji={Humble}
                heading={'UI/UX'}
                detail={'Figma, Adobe XD, Adobe After Effects'}
                />
            </div>
            <div className="blur s-blur2" style={{background: "var(--purple)"}}></div>
        </div>
    </div>
  )
}

export default Services
